package com.example.doctour.presentation.ui.fragments.authAndReg.signUpimport com.example.doctour.di.UserPreferencesimport kotlinx.coroutines.runBlockingimport okhttp3.Interceptorimport okhttp3.Responseimport javax.inject.Injectclass AuthInterceptor @Inject constructor(    val appPrefs: UserPreferences,) : Interceptor {    override fun intercept(chain: Interceptor.Chain): Response {        val token = runBlocking {            appPrefs.accessToken        }        val request = chain.request().newBuilder()        request.addHeader("Authorization", "Bearer $token")        return chain.proceed(request.build())    }}