package com.example.doctour.presentation.ui.fragments.main.aboutdoctorimport androidx.recyclerview.widget.DiffUtilimport com.example.doctour.presentation.model.ReviewUIclass DFUtilFeedback:DiffUtil.ItemCallback<ReviewUI> (){    override fun areItemsTheSame(oldItem: ReviewUI, newItem: ReviewUI): Boolean {        return oldItem.id == newItem.id    }    override fun areContentsTheSame(oldItem: ReviewUI, newItem: ReviewUI): Boolean {        return oldItem==newItem    }}