package com.example.doctour.presentation.ui.fragments.main.aboutdoctor.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.ListAdapterimport androidx.recyclerview.widget.RecyclerViewimport com.example.doctour.databinding.ItemFeedbacksBindingimport com.example.doctour.presentation.model.ReviewUiimport com.example.doctour.presentation.ui.fragments.main.aboutdoctor.DFUtilFeedbackclass FeedbacksAdapter : RecyclerView.Adapter<FeedbacksAdapter.FeedBacksViewHolder>() {    private var reviewList = arrayListOf<ReviewUi>()    fun addReview(list: List<ReviewUi>) {        reviewList.clear()        reviewList.addAll(list)        notifyDataSetChanged()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): FeedBacksViewHolder {        return FeedBacksViewHolder(            ItemFeedbacksBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: FeedBacksViewHolder, position: Int) {        holder.bind(reviewList[position])    }    override fun getItemCount(): Int {        return reviewList.size    }    inner class FeedBacksViewHolder(private var binding: ItemFeedbacksBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(reviewUi: ReviewUi) {            binding.tvName.text = reviewUi.doctor.toString()            binding.tvFeedback.text = reviewUi.text            binding.tvStars.text = reviewUi.stars.toString()        }    }}