package com.example.doctour.data.repositoriesimport com.example.doctour.data.base.BaseRepositoryimport com.example.doctour.data.remote.apiService.auth.AuthenticationApiServiceimport com.example.doctour.data.remote.dto.SignInDtoimport com.example.doctour.data.remote.dto.SignUpDtoimport javax.inject.Injectclass AuthenticationRepository @Inject constructor(    private val authenticationApiService: AuthenticationApiService) : BaseRepository() {    fun signUp(        name: String,        username: String,        email: String,        password: String,        password_again: String    ) = makeNetworkRequest {        authenticationApiService.signUp(            SignUpDto(                name,                username,                email,                password,                password_again            )        )    }    fun signIn(username: String, password: String) = makeNetworkRequest {        authenticationApiService.login(SignInDto(username, password))    }}